export function Wait(){

    const [isCopied, setIsCopied] = useState(false);
    const history = useHistory();

    const onCopyText = () => {
      setIsCopied(true);
      setTimeout(() => {
        setIsCopied(false);
      }, 1000);
    };


    return( <div className="wait">
    <h1 id="waittitle" className="title is-1">Várakozás a többi játékosra</h1>
      <div className="joinedplayers">
        <p id="waitcode">
          A szoba kódja: {roomId}
          <CopyToClipboard text={roomId} onCopy={onCopyText}>
            <div className="copy-area">
            <i class="fas fa-copy"></i>
            </div>
          </CopyToClipboard>
        </p>
        <p id="waitedplayer">Játékosok:</p>
        <ul>
          {players.map((p) =>
              <li className="waitinglayerlist">{p.name}</li>
          )}
        </ul>
        {players.map((p) =>
              p.boss === true && p.socketid == socket.id ? <button onClick={() => handleBossStarts()} className="button is-warning is-rounded" id="startbutton">Indítás</button> : <p></p>
        )}
      </div>
      <Footer />
    </div>);
}
