import { CopyToClipboard } from "react-copy-to-clipboard";
import { Footer } from "../Footer";
import { socket } from "../api/SocketApi";
import { useHistory } from "react-router";
import { useState } from "react";

import 'bulma/css/bulma.min.css';
import { BrowserRouter, Route, Switch } from "react-router-dom";
import reactDom from "react-dom";
import React from "react";


export function Wait({socket, setRoomId, roomId, players, setPlayers, game, setGame}){

    const [isCopied, setIsCopied] = useState(false);

    const onCopyText = () => {
      setIsCopied(true);
      setTimeout(() => {
        setIsCopied(false);
      }, 1000);
    };

    socket.on("state-changed", function (args) {
        if(args.roomId === roomId){
          let state = JSON.parse(args.state);
          setGame(state.game);
          setPlayers(state.players);
          if(state.game.gameStarted){
            history.push("/game");
          }
          for(let i = 0; i < state.players.length; i++){
            if(state.players[i].socketid === socket.id && currentPlayerNum === -1){
              setCurrentPlayerNum(i);
            }
          }
        }
      });

    function handleBossStarts(){
        game.gameStarted=true;
        setGame(game);
        socket.emit("sync-state", roomId,
            `{"game": ${JSON.stringify(game)}, "players": ${JSON.stringify(players)} }`, false,
            (response) => {
                console.log("state synced");
            }
        );
      }


    return( <div className="wait">
    <h1 id="waittitle" className="title is-1">Várakozás a többi játékosra</h1>
      <div className="joinedplayers">
        <p id="waitcode">
          A szoba kódja: {roomId}
          <CopyToClipboard text={roomId} onCopy={onCopyText}>
            <div className="copy-area">
            <i class="fas fa-copy"></i>
            </div>
          </CopyToClipboard>
        </p>
        <p id="waitedplayer">Játékosok:</p>
        <ul>
          {players.map((p) =>
              <p className="waitinglayerlist">{p.name}</p>
          )}
        </ul>
        {players.map((p) =>
              p.boss === true && p.socketid == socket.id ? <button onClick={() => handleBossStarts()} className="button is-warning is-rounded" id="startbutton">Indítás</button> : <p></p>
        )}
      </div>
      <Footer />
    </div>);
}
