export function Game({socket, setRoomId, roomId, players, setPlayers, game, setGame,}){

    const possibilities = ['♠︎','♡','♣','♦','Nincs adu!'];

    return(
    <div className="game">
        <div>
        <h1 className="gameadus">ADU: {possibilities[game.laps % 5]}</h1>
        <h1 className="gameadus">Lapok száma: {Math.floor(52/players.length)-game.laps}</h1>
        <h1 className="gameadus">Pontok</h1>
        {players.map((p) => 
            <p className="displaypoint">{p.name}: {p.point}</p>
        )}

        <input id="startnameinput" onChange={(event) => setPlayerName(event.target.value)} className="input is-rounded is-warning" type="text" placeholder="Játékos név"></input><br></br>
        <button id="startbtn" className="button is-warning is-rounded startbtn">Létrehozás</button>

        </div>
    </div>
)
}
