import { useState } from "react";
import React from 'react';

export function Game({socket, setRoomId, roomId, players, setPlayers, game, setGame,currentPlayerNum}){

    const possibilities = ['♠︎','♡','♣','♦','Nincs adu!'];
    const [tip, setTip] = useState(0);
    const [hit, setHit] = useState(0);

    return(
    <div className="game">
        <div>
        <h1 className="gameadus">ADU: {possibilities[game.laps % 5]}</h1>
        <h1 className="gameadus">Lapok száma: {Math.floor(52/players.length)-game.laps}</h1>
        <h1 className="gameadus">Pontok</h1>
        {players.map((p) => 
            <p className="displaypoint">{p.name}: {p.point}</p>
        )}

        <input id="tipinput" onChange={(event) => setTip(event.target.value)} className="input is-rounded is-warning" type="text" placeholder="Tipp"></input><br></br>
        <button id="tipbtn" onClick={() => confirmTip()} className="button is-warning is-rounded startbtn">Rögzítés</button>

        <h1 className="gameadus">Tippek</h1>
        {players.map((p) => 
            <p className="displaypoint">{p.name}: {p.tip}</p>
        )}

        <br />
        <input id="tipinput" onChange={(event) => setHit(event.target.value)} className="input is-rounded is-warning" type="text" placeholder="Találat"></input><br></br>
        <button id="tipbtn" className="button is-warning is-rounded startbtn">Rögzítés</button>

        </div>
    </div>
)
}
