import { useState } from "react";
import React from 'react';

export function Game({socket, setRoomId, roomId, players, setPlayers, game, setGame,currentPlayerNum}){

    const possibilities = ['♠︎','♡','♣','♦','Nincs adu!'];
    const [tip, setTip] = useState(0);
    const [hit, setHit] = useState(0);

    socket.on("state-changed", function (args) {
        if(args.roomId === roomId){
          let state = JSON.parse(args.state);
          setGame(state.game);
          setPlayers(state.players);
          if(currentPlayerNum === -1) {
            for(let i = 0; i < state.players.length; i++){
              if(state.players[i].socketid === socket.id){
                setCurrentPlayerNum(i);
              }
            }
          }
        }
      });

    function confirmTip(){
        players[currentPlayerNum].tip = tip;
        setPlayers(players);
        socket.emit("sync-state", roomId,
            `{"game": ${JSON.stringify(game)}, "players": ${JSON.stringify(players)} }`, false,
            (response) => {
                console.log("state synced");
            }
        );
    }

    function handleNext(){
        game.laps+=1;
        setGame(game);
        socket.emit("sync-state", roomId,
            `{"game": ${JSON.stringify(game)}, "players": ${JSON.stringify(players)} }`, false,
            (response) => {
                console.log("state synced");
            }
        );
    }

    return(
    <div className="game">
        <div>
        <h1 className="gameadus">ADU: {possibilities[game.laps % 5]}</h1>
        <h1 className="gameadus">Lapok száma: {Math.floor(52/players.length)-game.laps}</h1>
        <h1 className="gameadus">Pontok</h1>
        {players.map((p) => 
            <p key={p.id} className="displaypoint">{p.name}: {p.point}</p>
        )}

        <input id="tipinput" onChange={(event) => setTip(event.target.value)} className="input is-rounded is-warning" type="number" placeholder="Tipp"></input><br></br>
        <button id="tipbtn" onClick={() => confirmTip()} className="button is-warning is-rounded startbtn">Rögzítés</button>

        <h1 className="gameadus">Tippek</h1>
        {players.map((p) => 
            <p key={p.id} className="displaypoint">{p.name}: {p.tip}</p>
        )}


        <input id="tipinput" onChange={(event) => setHit(event.target.value)} className="input is-rounded is-warning" type="text" placeholder="Találat"></input><br></br>
        <button id="tipbtn" className="button is-warning is-rounded startbtn">Rögzítés</button>
        <br />
        {players.map((p) =>
              p.boss === true && p.socketid === socket.id ? <button key={p.id} onClick={() => handleNext()} className="button is-warning is-rounded" id="nextbutton">Következő kör</button> : ""
        )}

        </div>
    </div>
)
}
