import { Create } from "./Menu/Create";
import 'bulma/css/bulma.min.css';
import { Join } from "./Menu/Join";
import { Footer } from "./Footer";
import { Waves } from "./Menu/Waves";
import { socket } from "./api/SocketApi";
import { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import reactDom from "react-dom";
import React from "react";
import diamond from "./assets/diamond.svg"
import spade from "./assets/spade.svg"
import heart from "./assets/heart.svg"
import clubs from "./assets/clubs.svg"
import non from "./assets/non.svg"
import { CopyToClipboard } from "react-copy-to-clipboard";
import "../node_modules/@fortawesome/fontawesome-free/css/all.min.css"


function App() {

  const [roomId, setRoomId] = useState("");
  const [game, setGame] = useState({});
  const [players, setPlayers] = useState([]);
  const [currentPlayerNum, setCurrentPlayerNum] = useState(-1);
  const possibilities = ['♣','♠︎','♡','♦'];

  const [isCopied, setIsCopied] = useState(false);

  const onCopyText = () => {
    setIsCopied(true);
    setTimeout(() => {
      setIsCopied(false);
    }, 1000);
  };

  socket.on("state-changed", function (args) {
    if(args.roomId === roomId){
      let state = JSON.parse(args.state);
      setGame(state.game);
      setPlayers(state.players);
      for(let i = 0; i < state.players.length; i++){
        if(state.players[i].socketid === socket.id){
          setCurrentPlayerNum(i);
        }
      }
    }
  });

  return (
    <div className="App">
      <BrowserRouter>
      <Switch>
      <Route exact path="/">
      <div className="home">
        <div id="hero">
          <h1 id="maintile" className="title is-1">Adu App</h1>
          <div id="maintext" className="container">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit,
            sed do eiusmod tempor incididunt ut labore et dolore magna
            aliqua. Ut enim ad minim veniam, quis nostrud exercitation
            ullamco laboris nisi ut aliquip ex ea commodo consequat.
            Duis aute irure dolor in reprehenderit in voluptate velit
            esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
            occaecat cupidatat non proident, sunt in culpa qui officia
            deserunt mollit anim id est laborum.
          </div>
        </div>
        <Waves />
        <div className="mainbox">
          <Create socket={socket} setRoomId={setRoomId} roomId={roomId} players={players} setPlayers={setPlayers} game={game} setGame={setGame}/>
          <hr />
          <Join socket={socket} setRoomId={setRoomId} roomId={roomId} players={players} setPlayers={setPlayers} game={game} setGame={setGame}/>
        </div>
        <Footer />
      </div>
      </Route>

    <div className="wait">
      <h1 id="waittitle" className="title is-1">Várakozás a többi játékosra</h1>
        <div className="joinedplayers">
          <p id="waitcode">
            A szoba kódja: {roomId}
            <CopyToClipboard text={roomId} onCopy={onCopyText}>
              <div className="copy-area">
              <i class="fas fa-copy"></i>
              </div>
            </CopyToClipboard>
          </p>
          <p id="waitedplayer">Játékosok:</p>
          <ul>
            {players.map((p) =>
                <li className="waitinglayerlist">{p.name}</li>
            )}
          </ul>
          {players.map((p) =>
                p.boss === true && p.socketid == socket.id ? <button className="button is-warning is-rounded" id="startbutton">Indítás</button> : <p></p>
          )}
        </div>
        <Footer />
      </div>
      <div className="game">
          <div>
            <h1 className="gameadus">ADU: {possibilities[1]}</h1>
            <h1 className="gameadus">Lapok száma: 12</h1>
            <h1 className="gameadus">Pontok</h1>
              <p className="displaypoint">Gergo: 12</p>
              <p className="displaypoint">Martin: 12</p>
              <p className="displaypoint">Jani: 12</p>
            <h1 className="gameadus">ADU: {possibilities[1]}</h1>
            <h1 className="gameadus">ADU: {possibilities[1]}</h1>
            <h1 className="gameadus">ADU: {possibilities[1]}</h1>
          </div>
      </div>
      </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
